.navbar-custom {
    background-color: #00ABE4;
    font-weight: bold;
}

.card {
    border: 2px solid #000;
    width: 300px;
    margin: 5px;
    /* min-width: 280px; */
}

.card .card-description {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    opacity: 0;
    transition: opacity 0.3s;
    padding: 10px;
    background-color: rgba(0, 0, 0, 0.7);
    color: #fff;
}

.card:hover .card-description {
    opacity: 1;
}

